document.addEventListener('DOMContentLoaded',function(){if(window.innerWidth<=768){return;}const cursor=document.createElement('div');cursor.className='custom-cursor';document.body.appendChild(cursor);let mouseX=0;let mouseY=0;document.addEventListener('mousemove',function(e){mouseX=e.clientX;mouseY=e.clientY;cursor.style.left=(mouseX-12.5)+'px';cursor.style.top=(mouseY-12.5)+'px';});const clickableSelectors=[ 'a','button','input[type="button"]','input[type="submit"]','input[type="reset"]','.hover-target','.search-icon','.hamburger','.grid-item','.close-btn','.nav-project','.back-to-top-btn','.footer-center','.press-item','.fact-item','.suggestion-tag','.search-result-item','.fun-fact-tag','.mobile-nav a','.desktop-nav a','label','select','textarea','[role="button"]','[tabindex="0"]' ];function addHoverEffects(){clickableSelectors.forEach(selector=>{const elements=document.querySelectorAll(selector);elements.forEach(element=>{element.addEventListener('mouseenter',function(){cursor.classList.add('hover');});element.addEventListener('mouseleave',function(){cursor.classList.remove('hover');});});});}addHoverEffects();const observer=new MutationObserver(function(mutations){mutations.forEach(function(mutation){if(mutation.addedNodes.length>0){addHoverEffects();}});});observer.observe(document.body,{childList:true,subtree:true});document.addEventListener('mouseleave',function(){cursor.style.opacity='0';});document.addEventListener('mouseenter',function(){cursor.style.opacity='1';});document.addEventListener('visibilitychange',function(){if(document.hidden){cursor.style.opacity='0';}else{cursor.style.opacity='1';}});});